const clearZeros=async(e,a)=>{try{Knack.showSpinner();const r=a.map((a=>db(e.source.object).key(a.id).delete())),t=(await Promise.allSettled(r)).filter((e=>"rejected"==e.status)).map((e=>e.reason.toString())).join("<br>");if(t.length)throw Error(t);document.getElementsByClassName("close-modal")[0]?.click(),Knack.router.scene_view.model.views.models.map((e=>Knack.views[e.id].model)).filter((e=>"data"in e)).forEach((e=>e.fetch())),Knack.hideSpinner()}catch(e){await MsgBox("There was an error clearing the records!",`${e.name}: ${e.message}`)}};