async function updateDriveHours(e){let i=[],r=VWS.TripDetails.Timecards[0],d=db(r).key(e.id).get();d.forEach((e=>{let r=i.find((i=>i.id==e[FileReader.Timecards.Project]));r?r.count++:i.push({id:e.id,miles:e[FileReader.Projects.ProjectMiles],count:1})}));let s=i.reduce(((e,i)=>e+i.miles)),t=i.map((i=>({[i.id]:i.miles/s/i.count*e[FLD.Trips.DriveHours]})));return d.filter((e=>e[FLD.Timecards.DriveTime]!=t[e.id])).map((e=>db(TBL.Timecards).key(e.id).update({[FLD.Timecards.DriveTime]:e.driveHours})))}