async function setUnitPrice(n,t,e){try{Knack.showSpinner();const i=e.source.object,r=FLD[i].UnitPrice,a=FLD[i].CostUnit,c=FLD[i].CostUnitPrice;if(n[r]!=t[r])return;if(n[a]==t[a])return;let o=await db(i).key(n.id).update({[r]:n[c]});return Knack.hideSpinner(),o}catch(n){return await MsgBox("There was an error updating the unit price!",n),n}finally{navBack(0)}}